import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { connect } from "react-redux";
import formStyles from "../formElements/formStyles";
import { Grid, Typography, TextField, Divider } from "@material-ui/core";
import { TextFormField } from "../formElements/TextFormField.jsx";

const useStyles = makeStyles(theme => ({
  bottomBox: {
    padding: theme.spacing(2, 6, 1, 6)
  }
}));

const infoFieldData = [
  { label: "Grant Name", type: "text", name: "competition_name" },
  { label: "Website", type: "url", name: "website" },
  { label: "Amount", type: "number", name: "amount" },
  { label: "Amount Notes", type: "text", name: "amount_notes" }
];

const checkMultiline = arg => {
  return arg === "amount_notes" ? "multiline rows=3" : "";
};

// console.log(checkMultiline("amount_notes"));

export const GrantInfoForm = props => {
  const styles = useStyles();
  return (
    <Fragment>
      <Typography variant="h5" className={null}>
        Grant Info
      </Typography>
      <Divider variant="middle" />
      <Grid container spacing={3} className={styles.bottomBox}>
        {infoFieldData.map(data => (
          <TextFormField
            label={data.label}
            type={data.type}
            name={data.name}
            {...console.log(checkMultiline(data.name))}
          />
        ))}
      </Grid>
    </Fragment>
  );
};

