import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";

import {
  Grid,
  Typography,
  TextField,
  Divider,
  MenuItem
} from "@material-ui/core";
import formStyles from "../formElements/formStyles";
import { areaFocus } from "../formUtils/formValues.js";
import { TextFormField } from "./TextFormField";

const useStyles = makeStyles(theme => ({
  bottomBox: {
    padding: theme.spacing(2, 6, 1, 6)
  }
}));

const FocusFormData = [
  { label: "Sponsoring Entity", type: "text", name: "sponsoring_entity" },
  {
    label: "Grant Categories",
    name: "area_focus",
    select: true,
    data: areaFocus
  }
];
console.log("areaFocus", FocusFormData);

export const GrantFocusForm = props => {
  const styles = useStyles();

  return (
    <Fragment>
      <Typography variant="h5">Grant Focus</Typography>
      <Divider variant="middle" />
      <Grid container spacing={3} className={styles.bottomBox}>
        {FocusFormData.map(data => {
          console.log("data", data);
          return (
            <TextFormField
              label={data.label}
              type={data.type}
              name={data.name}
              select={data.select}
              data={data.data}
            />
          );
        })}
        <Grid item xs={12}></Grid>
      </Grid>
    </Fragment>
  );
};

