import React, { useState, useEffect, useContext } from "react";
import { useSelector } from "react-redux";
import { UserSettingsForm } from "./UserSettingsForm.js";
import { UserData } from "./userData.js";
import { Container, Button } from "@material-ui/core";
import { ActionsContext } from "../../context/ActionsContext";
import { makeStyles } from "@material-ui/core/styles";
import { AuthForm } from "./AuthForm.js";
import {useForm} from "../../hooks/useForm";

const useStyles = makeStyles(theme => ({
  button: {
    margin: "2em"
  }
}));

const UserSettings = () => {
  const actions = useContext(ActionsContext);
  const [isEditing, setIsEditing] = useState(false);
  const { token, currentUser, pgUser } = useSelector(state => state.user);
  const styles = useStyles();
  const [values, handleChange, handleSubmit] = useForm({
        first_name: '',
        last_name: '',
        role: '',
        phone_number: '',
        company: '',
        company_url: '',
        about: ''
      }
      , doSubmit);

  function doSubmit() {
    actions.user.updateUser(token, pgUser.id, values);
  }

  return (
    <React.Fragment>
      <Container maxWidth="lg">
        {/* <AuthForm /> */}
        {/* <UserSettingsForm /> */}
        {!isEditing ? <UserData /> : <UserSettingsForm values={values} handleChange={handleChange} handleSubmit={handleSubmit}/>}
        {!isEditing && (
          <Button
            variant="contained"
            color="primary"
            className={styles.button}
            disabled
            onClick={() => !isEditing && setIsEditing(true)}
          >
            Edit Details
          </Button>
        )}
        {isEditing && (
          <>
            <Button
              variant="contained"
              color="primary"
              className={styles.button}
              onClick={handleSubmit}
            >
              Save Changes
            </Button>
            <Button
              variant="contained"
              color="secondary"
              className={styles.button}
              onClick={() => isEditing && setIsEditing(false)}
            >
              Cancel Edit
            </Button>
          </>
        )}
      </Container>
    </React.Fragment>
  );
};

export default UserSettings;
