import React, { Fragment } from "react";
import { makeStyles } from "@material-ui/core/styles";

import {
  Grid,
  Typography,
  TextField,
  Divider,
  MenuItem
} from "@material-ui/core";
import formStyles from "../formElements/formStyles";
import { areaFocus } from "../formUtils/formValues.js";
import { TextFormField } from "./TextFormField";

const useStyles = makeStyles(theme => ({
  bottomBox: {
    padding: theme.spacing(2, 6, 1, 6)
  }
}));

const FocusFormData = [
  { label: "Sponsoring Entity", type: "text", name: "sponsoring_entity" },
  { label: "Grant Categories", name: "area_focus", select: true }
];

export const GrantFocusForm = props => {
  const styles = useStyles();

  return (
    <Fragment>
      <Typography variant="h5">Grant Focus</Typography>
      <Divider variant="middle" />
      <Grid container spacing={3} className={styles.bottomBox}>
        {FocusFormData.map(data => {
          <TextFormField
            label={data.label}
            type={data.type}
            name={data.name}
          />;
        })}
        <Grid item xs={12}>
          <TextField
            label="Grant Categories"
            select
            name="area_focus"
            placeholder="Area Focus"
            fullWidth
          >
            {/* Maps through the array to return values for dropdown */}

            {areaFocus.map(option => (
              <MenuItem key={option.value} value={option.value}>
                {option.label}
              </MenuItem>
            ))}
          </TextField>
        </Grid>
      </Grid>
    </Fragment>
  );
};

